/**
 * Contains all the options. Emits events when changes occur.
 * @class Options
 */
class Options extends events.EventEmitter {
  constructor(options = {}) {
    super(options);

    this._values = options;
  }

  /**
   * Emits that a change has happened.
   * @param key
   * @param value
   * @private
   */
  _emitChange(key, value) {
    this.emit(`change:${key}`, value);
    this.emit(`change`, value);
  }

  /**
   * Gets the (nested) key from the object.
   * @param {String} key
   * @returns {*}
   */
  get(key) {
    return _.get(this._values, key);
  }

  /**
   * Sets the value of the (nested) key to `value`.
   * @param {String} key
   * @param {*} value
   * @returns {Options}
   */
  set(key, value) {
    _.set(this._values, key, value);

    this._emitChange(key, value);

    return this;
  }

  /**
   * Sets the value of the (nested) key to `undefined`. Does not delete the key.
   * @param {String} key
   * @returns {Options}
   */
  del(key) {
    _.set(this._values, key, undefined);

    this._emitChange(key, 'undefined');

    return this;
  }

  /**
   * Merges `object` into the current options, overriding values.
   * TODO: use _.defaultsDeep
   * @param {Object} object
   * @returns {Options}
   */
  merge(object) {
    return this;
  }
}

import _ from 'lodash';

/**
 * Represents one thing to be done. May be a long running task.
 * TODO: add a removeDependencies(array) method.
 * @class Task
 */
class Task {
  constructor(options = {}) {
    this.options = options;

    this.builder = options.builder;

    this.dependencies = options.dependencies || [];
  }

  /**
   * Adds a dependency.
   * @param {String} name The name of the dependency.
   * @return Task The instance.
   */
  addDependency(name) {
    this.dependencies.push(name);
    this.dependencies = _.uniq(this.dependencies);

    return this;
  }

  /**
   * Removes a dependency.
   * @param {String} name The name of the dependency.
   * @return Task The instance.
   */
  removeDependency(name) {
    this.dependencies = _.without(this.dependencies, name);

    return this;
  }

  /**
   * Adds an Array of strings as dependencies.
   * @param {Array} dependencies An array of strings.
   * @return Task The instance.
   */
  addDependencies(dependencies) {
    _.each(dependencies, (dependency) => {
      this.addDependency(dependency);
    });

    return this;
  }

  /**
   * Starts the task.
   * @param {Function} cb The callback function.
   */
  start(cb) {
    if (!this.run) {
      throw new Error('No run function set.');
    }

    // TODO: This is horrible!
    this.__proto__.run.call(this, cb);
  }
}

export default Task;

