import _ from 'lodash';
import lsr from 'lsr';

function getFiles(path, cb) {
  lsr(path, (e, nodes) => {
    if (e) {
      return cb(e);
    }

    cb(null, _(nodes)
      .filter(v => v.isFile() && v)
      .value());
  });
}

export default getFiles;

import chalk from 'chalk';
import log from 'loglevel';

const arrowCharacter = '';
const okCharacter = '';
const warningCharacter = '';

function taskInfo(task, message) {
  log.info(`  ${task} ${chalk.grey(message)}`);
}

function taskWarn(task, message) {
  log.warn(`${chalk.yellow(arrowCharacter)} ${task}: ${message}`);
}

function taskError(task, message) {
  log.error(`${chalk.red(arrowCharacter)} ${task}: ${message}`);
}

function disabledTask(name) {
  log.warn(`${chalk.yellow(warningCharacter)} ${name}`);
}

function startTask(name) {
  log.info(`${chalk.grey(arrowCharacter)} ${name}`);
}

function skipTask(name) {
  log.info(`${chalk.grey(okCharacter)} ${name}`);
}

function finishTask(name) {
  log.info(`${chalk.green(okCharacter)} ${chalk.green(name)}`);
}

export default {
  arrowCharacter,
  okCharacter,
  warningCharacter,

  taskInfo,
  taskWarn,
  taskError,
  disabledTask,
  startTask,
  skipTask,
  finishTask
};

import _ from 'lodash';

function parseOptions(x, y = {}) {
  const z = {};

  _.each(x, function(xValue, xKey) {
    const yValue = y[xKey];

    if (_.isObject(xValue) && !_.isArray(xValue)) {
      z[xKey] = parseOptions(xValue, yValue);
    } else {
      if (!_.isUndefined(yValue)) {
        z[xKey] = yValue;
      } else {
        z[xKey] = xValue;
      }
    }
  });

  return z;
}

export default parseOptions;

